import { useState } from "react"

function AddTodoBar({ onAddItem }) {


  return (
    <>
      <h1>Add an Item</h1>
      <form>
        <input value="Type item here..." type="text"></input>
        <button onClick={onAddItem}> Add </button>
      </form>
    </>
  );
}

function ItemRow({ item }) {
  return (
    <tr>
      <td>{item.id}</td>
      <td>{item.item}</td>
      <td>{item.status}</td>
      <td>
        <button>Edit</button>
        <button>delete</button>
      </td>
    </tr>
  );

}

function ListTable({ list }) {
  const rows = [];

  list.forEach((item) => {
    rows.push(
      <ItemRow item={item} key={item.id} />
    )
  }
  )

  return (
    <>
      <h2>Todo List</h2>
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Item</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {rows}
        </tbody>
      </table>
    </>
  );
}

export default function App() {
  const [items, setItems] = useState([{ id: 0, item: "example", status: "active" }, { id: 1, item: "another example", status: "active" }]);

  return (
    <>
      <AddTodoBar onAddItem={setItems} />
      <hr></hr>
      <ListTable list={items} />
    </>
  );
}
