import { useState } from "react"

function AddTodoBar({ items, setItems }) {
  const [newItem, setNewItem] = useState('');

  function handleClick() {
    if (newItem == '') return;

    const newItemObject = {
      id: null, item: '', statue: ''
    };
    console.log("new item: " + newItem);

    //preapre the newItem
    newItemObject = {
      id: items.length, item: newItem, statue: 'active'
    };
    console.log(items.length);

    // add to items 
    const nextItems = items.push(newItemObject);
    setItems(nextItems);

  }

  return (
    <>
      <h1>Add an Item</h1>
      <input type="text" value={newItem} onChange={e => setNewItem(e.target.value)} placeholder="Type item here..." ></input>
      <button onClick={() => handleClick()}> Add </button>
      <p>new item: {newItem}</p>
    </>
  );
}

function ItemRow({ item }) {
  return (
    <tr>
      <td>{item.id}</td>
      <td>{item.item}</td>
      <td>{item.status}</td>
      <td>
        <button>Edit</button>
        <button>delete</button>
      </td>
    </tr>
  );

}

function ListTable({ list }) {
  const rows = [];

  list.forEach((item) => {
    rows.push(
      <ItemRow item={item} key={item.id} />
    )
  }
  )

  return (
    <>
      <h2>Todo List</h2>
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Item</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {rows}
        </tbody>
      </table>
    </>
  );
}

export default function App() {
  const [items, setItems] = useState([{ id: 0, item: "example", status: "active" }, { id: 1, item: "another example", status: "active" }]);

  return (
    <>
      <AddTodoBar items={items} setItems={setItems} />
      <hr></hr>
      <ListTable list={items} />
    </>
  );
}
